import csvimport osclass Evaluation:        def __init__(self, system, model):                self.system = system                self.model = model        self.results = model.results                self.createDirectories()        self.printModelInstance()        self.printResults()        def createDirectories(self):                nameDir = './outputs/results/'        try:            os.makedirs(nameDir)        except OSError:            pass                def printModelInstance(self):                fileName = self.system['folderOutput'] + 'print_model_instance.txt'         with open(fileName, 'w') as f:            self.model.instance.pprint(ostream=f)            f.close()                fileName = self.system['folderOutput'] + 'model_instance.txt'         with open(fileName, 'w') as f:            self.model.instance.display(ostream=f)            f.close()                def printResults(self):                fileName = self.system['folderOutput'] + 'results.txt'         with open(fileName, 'w') as f:            self.model.instance.display(ostream=f)            f.close()